DROP TABLE IF EXISTS "producer" CASCADE;CREATE TABLE producer(nr INT,country VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "product" CASCADE;CREATE TABLE product(nr INT,producer INT,PRIMARY KEY (nr));DROP TABLE IF EXISTS "vendor" CASCADE;CREATE TABLE vendor(nr INT,country VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "offer" CASCADE;CREATE TABLE offer(nr INT,product INT,vendor INT,price VARCHAR,validTo DATE,deliveryDays INT,publisher INT,PRIMARY KEY (nr));DROP TABLE IF EXISTS "person" CASCADE;CREATE TABLE person(nr INT,country VARCHAR,PRIMARY KEY (nr));