DROP TABLE IF EXISTS "productfeature" CASCADE;CREATE TABLE productfeature(nr INT,label VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "producttype" CASCADE;CREATE TABLE producttype(nr INT,label VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "producer" CASCADE;CREATE TABLE producer(nr INT,label VARCHAR,country VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "product" CASCADE;CREATE TABLE product(nr INT,label VARCHAR,producer INT,PRIMARY KEY (nr));DROP TABLE IF EXISTS "vendor" CASCADE;CREATE TABLE vendor(nr INT,label VARCHAR,country VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "offer" CASCADE;CREATE TABLE offer(nr INT,product INT,vendor INT,price VARCHAR,validTo DATE,publisher INT,PRIMARY KEY (nr));DROP TABLE IF EXISTS "person" CASCADE;CREATE TABLE person(nr INT,name VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "review" CASCADE;CREATE TABLE review(nr INT,product INT,person INT,title VARCHAR,rating1 INT,rating2 INT,PRIMARY KEY (nr));