DROP TABLE IF EXISTS "product" CASCADE;CREATE TABLE product(nr INT,producer INT,PRIMARY KEY (nr));DROP TABLE IF EXISTS "person" CASCADE;CREATE TABLE person(nr INT,name VARCHAR,PRIMARY KEY (nr));DROP TABLE IF EXISTS "review" CASCADE;CREATE TABLE review(nr INT,product INT,person INT,reviewDate DATE,title VARCHAR,text VARCHAR,rating1 INT,rating2 INT,rating3 INT,rating4 INT,PRIMARY KEY (nr));